def getWord():
    return random.choice(word_list).upper()

def hangmanGame(word):
    guessable = "_ " * len(word)
    guessed = False
    word_guessed = []
    tries = 6
    letter_guessed = []
    print("Let's play Hangman\n")
    print(displayHangman(tries),"\n")
    print(guessable)
    while not guessed and tries > 0:
        print(f"\nYou have {tries} Tries left")
        guess1 = input("Enter your guess: ").upper()
        if len(guess1) == 1 and guess.isalpha():
            if guess1 in letter_guessed:
                print(f"You already guessed {guess}")
            elif guess1 not in word:
                print(f"{guess} is not in the word")
                tries -= 1
                letter_guessed.append(guess1)
            else:
                print(f"Great you found a letter {guess} is in the word")
                letter_guessed.append(guess1)
                wordAsList = guessable.split()
                indices = [i for i, l in enumerate(word) if l == guess1]
                for index in indices:
                    wordAsList[index] = guess1
                guessable = " ".join(wordAsList)
                if "_" not in guessable:
                    guessed = True
        elif len(guess1) == len(word):
            if guess1 in word_guessed:
                print(f"You already guessed {guess}")
            elif guess1 != word:
                print(f"{guess} is not the word")
                tries -= 1
                word_guessed,append(guess)
            else:
                guessed = True
                guessable = word
        else:
            print("Not a valid guess!")
        print(displayHangman(tries),"\n")
        print(guessable)
        
    if guessed:
        print("\nGreat you guessed the word!  *You Win*")
    else:
        print(f"\nYou ran out of tries, the word was {word}, Try again")

def displayHangman(tries):
    hang = {6:"\n|_ _ _ _\n|\t|\n|\n|\n|\n|\n|\n|\n|",
            5:"\n|_ _ _ _\n|\t|\n|\t0\n|\n|\n|\n|\n|\n|",
            4:"\n|_ _ _ _\n|\t|\n|\t0\n|\t|\n|\t|\n|\n|\n|\n|",
            3:"\n|_ _ _ _\n|\t|\n|     \ 0 /\n|      \|/\n|\t|\n|\n|\n|\n|",
            2:"\n|_ _ _ _\n|\t|\n|     \ 0 /\n|      \|/\n|\t|\n|\t|\n|\t|\n|\n|",
            1:"\n|_ _ _ _\n|\t|\n|     \ 0 /\n|      \|/\n|\t|\n|\t|\n|\t|\n|      / \\\n|     /   \\",
            0:"\n|_ _ _ _\n|\t|\n|     \ 0 /\n|      \|/\n|\t|\n|\t|\n|\t|\n|      / \\\n|     /   \\"
    }
    return hang[tries]

def main():
    again = "Y"
    while again == "Y":
        word = getWord()
        hangmanGame(word)
        again = input("\nPlay Again? (Y/N) ").upper()


main()
